import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ContestDetails = () => {
  const { id } = useParams();
  const [contest, setContest] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchContest = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contests/${id}`);
        setContest(response.data);  // Store contest data
      } catch (err) {
        setError('Failed to load contest details.');
      }
    };

    fetchContest();
  }, [id]);

  if (error) return <p>{error}</p>;

  return (
    <div>
      {contest ? (
        <div>
          <h2>{contest.name}</h2>
          <p><strong>Type:</strong> {contest.type}</p>
          <p><strong>Date:</strong> {new Date(contest.date).toLocaleDateString()}</p>
          <p><strong>Time:</strong> {contest.time}</p>
          <p><strong>Description:</strong> {contest.description}</p>
          {contest.file && <a href={`http://localhost:5000/${contest.file}`} target="_blank" rel="noopener noreferrer">Download File</a>}
        </div>
      ) : (
        <p>Loading contest details...</p>
      )}
    </div>
  );
};

export default ContestDetails;
