import React, { useState } from 'react';
import axios from 'axios';
import './Modal.css';

const Modal = ({ closeModal, type, onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      // Determine the endpoint based on the modal type (login or register)
      const endpoint = type === 'login' ? '/api/auth/' : '/api/auth/register';
      const response = await axios.post(`http://localhost:5000${endpoint}`, { email, password });
  
      // Store token in localStorage on successful login/registration
      localStorage.setItem('token', response.data.token);
  
      // Trigger the onLogin callback passed as a prop to the modal
      onLogin();
  
      // Close the modal after successful authentication
      closeModal();
    } catch (err) {
      setError(type === 'login' ? 'Invalid credentials' : 'Registration failed');
      console.error(err); // Log error for debugging purposes
    }
  };
  

  return (
    <div className="modal-overlay" onClick={closeModal}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>{type === 'login' ? 'Login' : 'Register'}</h2>
        <form onSubmit={handleSubmit}>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {error && <p className="error">{error}</p>}
          <button type="submit">{type === 'login' ? 'Login' : 'Register'}</button>
        </form>
        <button className="close-btn" onClick={closeModal}>Close</button>
      </div>
    </div>
  );
};

export default Modal;
