import React from 'react';
import './Home.css';

const Home = ({ isLoggedIn, onLogout }) => {
  // Define the contests constant here
  const contests = [
    { id: 1, name: "Weekly Contest 1", type: "Weekly", date: "2024-11-20" },
    { id: 2, name: "Bi-Daily Contest 1", type: "Bi-Daily", date: "2024-11-15" },
    { id: 3, name: "Special Quiz", type: "Quiz", date: "2024-11-25" },
  ];

  return (
    <div className="home-container">
      {/* Left Panel */}
      <div className="left-panel">
        <h3>Upcoming Contests</h3>
        <ul>
          {contests.map((contest) => (
            <li key={contest.id}>
              <strong>{contest.name}</strong> <br />
              Date: {new Date(contest.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header className="home-header">
          <h1>Welcome to Quizoo!</h1>
          <p>Your go-to platform for coding contests and quizzes.</p>
          <p>
            Join our coding club and enhance your skills with regular contests
            and fun activities.
          </p>
        </header>

        {/* Contest Cards */}
        <div className="contest-cards">
          {contests.map((contest) => (
            <div className="contest-card" key={contest.id}>
              <h3>{contest.name}</h3>
              <p>Type: {contest.type}</p>
              <p>Date: {new Date(contest.date).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      </div>

      {/* User Login Section */}
      <div className="user-section">
        {isLoggedIn ? (
          <>
            <p>Welcome back, User!</p>
            <button onClick={onLogout}>Logout</button>
          </>
        ) : (
          <button onClick={onLogout}>Login</button>
        )}
      </div>
    </div>
  );
};

export default Home;
