import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './CreateContest.css';

const CreateContest = () => {
  const [name, setName] = useState('');
  const [type, setType] = useState('');
  const [date, setDate] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation: Check if all fields are filled
    if (!name || !type || !date) {
      setError('Please fill in all fields.');
      return;
    }

    // Additional validation: Contest date should not be in the past
    const currentDate = new Date();
    const contestDate = new Date(date);
    if (contestDate < currentDate) {
      setError('The contest date cannot be in the past.');
      return;
    }

    const contestData = { name, type, date };

    try {
      // Make an API call to create the contest
      const response = await axios.post(
        process.env.REACT_APP_API_URL || 'http://localhost:5000/api/contests', 
        contestData
      );

      // Reset form fields and show success message
      setName('');
      setType('');
      setDate('');
      setError('');
      setSuccess(true);

      // Redirect to homepage after 2 seconds
      setTimeout(() => {
        navigate('/'); 
      }, 2000);

    } catch (err) {
      // Handle API error
      const errorMessage = err.response ? err.response.data.message : 'Error creating contest. Please try again later.';
      setError(errorMessage);
    }
  };

  return (
    <div className="create-contest-container">
      <h2>Create a New Contest</h2>

      {error && <p className="error">{error}</p>}
      {success && <p className="success-message">Contest created successfully!</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Contest Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="type">Contest Type:</label>
          <input
            type="text"
            id="type"
            value={type}
            onChange={(e) => setType(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="date">Contest Date:</label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <button type="submit">Create Contest</button>
      </form>
    </div>
  );
};

export default CreateContest;
