import React, { useState } from 'react';
import axios from 'axios';
import './Modal.css';

const Modal = ({ closeModal, type, onLogin }) => {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (type === 'login') {
      // Login Logic (allow email or username)
      try {
        const loginData = email || username;  // Send email or username based on what's filled
        const response = await axios.post('http://localhost:5000/api/auth/login', { login: loginData, password });
        localStorage.setItem('token', response.data.token);
        onLogin();
        closeModal();
      } catch (err) {
        setError('Invalid credentials');
      }
    } else if (type === 'register') {
      // Register Logic (with username)
      try {
        const response = await axios.post('http://localhost:5000/api/auth/register', { email, username, password });
        localStorage.setItem('token', response.data.token);
        onLogin();
        closeModal();
      } catch (err) {
        setError('Registration failed');
      }
    }
  };

  return (
    <div className="modal-overlay" onClick={closeModal}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>{type === 'login' ? 'Login' : 'Register'}</h2>
        <form onSubmit={handleSubmit}>
          {type === 'register' && (
            <>
              <label>Username:</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </>
          )}
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {error && <p className="error">{error}</p>}
          <button type="submit">{type === 'login' ? 'Login' : 'Register'}</button>
        </form>
        <button className="close-btn" onClick={closeModal}>Close</button>
      </div>
    </div>
  );
};

export default Modal;
