import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [contests, setContests] = useState([]);
  const [newContest, setNewContest] = useState({ title: '', description: '', type: '' });

  useEffect(() => {
    const fetchContests = async () => {
      const response = await axios.get('http://localhost:5000/api/contests');
      setContests(response.data);
    };
    fetchContests();
  }, []);

  const handleAddContest = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/admin/contests', newContest);
      setContests([...contests, response.data]);
      setNewContest({ title: '', description: '', type: '' });
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteContest = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/contests/${id}`);
      setContests(contests.filter((contest) => contest._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <form onSubmit={handleAddContest}>
        <input
          type="text"
          placeholder="Contest Title"
          value={newContest.title}
          onChange={(e) => setNewContest({ ...newContest, title: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={newContest.description}
          onChange={(e) => setNewContest({ ...newContest, description: e.target.value })}
          required
        />
        <select
          value={newContest.type}
          onChange={(e) => setNewContest({ ...newContest, type: e.target.value })}
          required
        >
          <option value="">Select Type</option>
          <option value="weekly">Weekly</option>
          <option value="bi-daily">Bi-Daily</option>
        </select>
        <button type="submit">Add Contest</button>
      </form>
      <h3>Existing Contests</h3>
      <ul>
        {contests.map((contest) => (
          <li key={contest._id}>
            {contest.title} - {contest.type}
            <button onClick={() => handleDeleteContest(contest._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
