// ContestDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ContestDetails = () => {
  const { contestId } = useParams();  // Get contest ID from URL
  const [contest, setContest] = useState(null);

  useEffect(() => {
    const fetchContestDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/contests/${contestId}`);
        setContest(response.data);
      } catch (error) {
        console.error('Error fetching contest details', error);
      }
    };
    fetchContestDetails();
  }, [contestId]);

  if (!contest) {
    return <div>Loading...</div>;  // Show a loading state until the contest details are fetched
  }

  return (
    <div>
      <h2>Contest Details</h2>
      <p><strong>Name:</strong> {contest.name}</p>
      <p><strong>Type:</strong> {contest.type}</p>
      <p><strong>Date:</strong> {new Date(contest.date).toLocaleDateString()}</p>
      {/* You can also display more details if needed */}
    </div>
  );
};

export default ContestDetails;
