import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './CreateContest.css';

const CreateContest = () => {
  const [name, setName] = useState('');
  const [type, setType] = useState('');
  const [date, setDate] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !type || !date) {
      setError('Please fill in all fields');
      return;
    }

    const contestData = { name, type, date };

    try {
      const response = await axios.post('http://localhost:5000/api/contests', contestData);

      setName('');
      setType('');
      setDate('');
      setError('');
      setSuccess(true);

      setTimeout(() => {
        navigate('/'); 
      }, 2000);
    } catch (err) {
      console.error(err);
      setError('Error creating contest, please try again.');
    }
  };

  return (
    <div className="create-contest-container">
      <h2>Create a New Contest</h2>
      {error && <p className="error">{error}</p>}
      {success && <p style={{ color: 'green', textAlign: 'center' }}>Contest created successfully!</p>}
      
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Contest Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        
        <div>
          <label htmlFor="type">Contest Type:</label>
          <input
            type="text"
            id="type"
            value={type}
            onChange={(e) => setType(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="date">Contest Date:</label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <button type="submit">Create Contest</button>
      </form>
    </div>
  );
};

export default CreateContest;
