import React from 'react';
import { Card, Container, Row, Col } from 'react-bootstrap';
import './Home.css';

const Home = ({ contests, isLoggedIn, onLogout }) => {
  return (
    <Container fluid className="home-container">
      <Row>
        {/* Left Panel */}
        <Col md={3} className="left-panel">
          <h3>Upcoming Contests</h3>
          <ul>
            {contests.map((contest) => (
              <li key={contest.id}>
                <strong>{contest.name}</strong> <br />
                Date: {contest.date}
              </li>
            ))}
          </ul>
        </Col>

        {/* Main Content */}
        <Col md={9} className="main-content">
          <header className="home-header">
            <h1>Welcome to Quizoo!</h1>
            <p>Your go-to platform for coding contests and quizzes.</p>
            <p>
              Join our coding club and enhance your skills with regular contests and fun activities.
            </p>
          </header>

          <Row className="contest-cards">
            {contests.map((contest) => (
              <Col md={4} key={contest.id} className="mb-4">
                <Card className="contest-card">
                  <Card.Body>
                    <Card.Title>{contest.name}</Card.Title>
                    <Card.Text>
                      Type: {contest.type} <br />
                      Date: {contest.date}
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default Home;
